import React from 'react';

class ProdutoInfo extends React.Component {
  trocaImg = (url) => {
    const imgPrincipal = document.querySelector('.product-info-image img');
    imgPrincipal.src = url;
  };
  constructor(props) {
    super(props);
    this.state = {
      tamanhoSelecionado: null,
    };
  }

  selecionarTamanho = (tamanho) => {
    const { tamanhoSelecionado } = this.state;
    if (tamanhoSelecionado === tamanho) {
      this.setState({ tamanhoSelecionado: null });
    } else {
      this.setState({ tamanhoSelecionado: tamanho });
    }
  };

  render() {
    const { tamanhoSelecionado } = this.state;
    return (
      <section className="product-info">
        <div className='div-imgCanto'>
          <img className='imgCanto' onClick={() => this.trocaImg('https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_2_2305526d0db0efb2bce9e8c3ab9d8bc1.jpg')} src='https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_2_2305526d0db0efb2bce9e8c3ab9d8bc1.jpg' alt="Imagem 1" />
          <img className='imgCanto' onClick={() => this.trocaImg('https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_3_35730bc959587bccea549bcbced8006c.jpg')} src='https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_3_35730bc959587bccea549bcbced8006c.jpg' alt="Imagem 2" />
          <img className='imgCanto' onClick={() => this.trocaImg('https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_2_2305526d0db0efb2bce9e8c3ab9d8bc1.jpg')} src='https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_2_2305526d0db0efb2bce9e8c3ab9d8bc1.jpg' alt="Imagem 3" />
          <img className='imgCanto' onClick={() => this.trocaImg('https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_1_64bea272f5cb6533995e6283efb0c841.jpg')} src='https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_1_64bea272f5cb6533995e6283efb0c841.jpg' alt="Imagem 4" />
        </div>
        <div className="product-info-image">
          <img src="https://images.tcdn.com.br/img/img_prod/1191248/kit_3_camisetas_basicas_pretas_masculinas_100_algodao_premium_malha_30_1_penteada_73_3_35730bc959587bccea549bcbced8006c.jpg" alt="Camisa" />
        </div>
        <div className="product-info-details">
          <p>Loja DUCAM</p>
          <h2>Camiseta Básica</h2>
          <span className='midleLine'>R$ 27,20</span> <span>R$ 20,95</span><br></br><span>3x de R$ 6,98 sem juros</span><br></br>
          <span><strong className='fs'>R$19,90</strong> no boleto/pix</span>
          <div className='stars'>
            <span className="material-symbols-outlined">star</span>
            <span className="material-symbols-outlined">star</span>
            <span className="material-symbols-outlined">star</span>
            <span className="material-symbols-outlined">star</span>
            <span className="material-symbols-outlined">star</span>
          </div>

          <ul className="ul-tamanho">
          <li
            onClick={() => this.selecionarTamanho('M')}
            style={{
              color: tamanhoSelecionado === 'M' ? '#fff' : '#000',
              backgroundColor: tamanhoSelecionado === 'M' ? '#000' : 'transparent',
            }}
          >
            P
          </li>
          <li
            onClick={() => this.selecionarTamanho('G')}
            style={{
              color: tamanhoSelecionado === 'G' ? '#fff' : '#000',
              backgroundColor: tamanhoSelecionado === 'G' ? '#000' : 'transparent',
            }}
          >
            M
          </li>
          <li
            onClick={() => this.selecionarTamanho('GG')}
            style={{
              color: tamanhoSelecionado === 'GG' ? '#fff' : '#000',
              backgroundColor: tamanhoSelecionado === 'GG' ? '#000' : 'transparent',
            }}
          >
            G
          </li>
          <li
            onClick={() => this.selecionarTamanho('P')}
            style={{
              color: tamanhoSelecionado === 'P' ? '#fff' : '#000',
              backgroundColor: tamanhoSelecionado === 'P' ? '#000' : 'transparent',
            }}
          >
            GG
          </li>
        </ul>
          <button>Adicionar a Sacola<span className="material-symbols-outlined">local_mall</span></button>
          <p>Apresentamos a camiseta perfeita para o seu guarda-roupa versátil. Com uma paleta de cores selecionada, este básico é adequado para qualquer ocasião. Produzida com excelência no Brasil, incorpora o renomado padrão de qualidade da marca Reserva. Adquira a sua camiseta Reserva hoje e desfrute do conforto e da elegância.</p>
        </div>
      </section>
    );
  }
}

export default ProdutoInfo;
