import React from 'react';
import './App.css';

class Perguntas extends React.Component {
  render() {
    const perguntas = [
      {
        id: 1,
        usuario: 'Usuário 1',
        dataPublicacao: '2023-06-20',
        duvida: 'Qual é a capacidade de armazenamento?',
        resposta: 'A capacidade de armazenamento é de 128GB.',
      },
      {
        id: 2,
        usuario: 'Usuário 2',
        dataPublicacao: '2023-06-18',
        duvida: 'O produto possui garantia?',
        resposta: 'Sim, o produto possui garantia de 1 ano.',
      },
      {
        id: 3,
        usuario: 'Usuário 3',
        dataPublicacao: '2023-06-15',
        duvida: 'O produto é compatível com Windows?',
        resposta: 'Sim, o produto é compatível com Windows.',
      },
      {
        id: 4,
        usuario: 'Usuário 4',
        dataPublicacao: '2023-06-12',
        duvida: 'Qual é a duração da bateria?',
        resposta: 'A bateria tem duração de até 8 horas.',
      },
    ];

    return (
      <div>
        <h3 className="perguntas-title">Perguntas de outros usuários</h3>
        <ul className="perguntas-list">
          {perguntas.map(pergunta => (
            <li key={pergunta.id} className="pergunta-item">
              <div className="pergunta-usuario">Usuário: {pergunta.usuario}</div>
              <div className="pergunta-data">Data de publicação: {pergunta.dataPublicacao}</div>
              <div className="pergunta-duvida">Dúvida: {pergunta.duvida}</div>
              <div className="pergunta-resposta">Resposta: {pergunta.resposta}</div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default Perguntas;
